import * as React from "react";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import Menu from "@mui/material/Menu";
import MenuIcon from "@mui/icons-material/Menu";
import Avatar from "@mui/material/Avatar";
import MenuItem from "@mui/material/MenuItem";
import styled from "styled-components";
import { Link } from "react-router-dom";
import KeyboardArrowRightIcon from "@mui/icons-material/KeyboardArrowRight";
import SearchIcon from "@mui/icons-material/Search";
import LocationOnIcon from "@mui/icons-material/LocationOn";
import ShoppingCartOutlinedIcon from "@mui/icons-material/ShoppingCartOutlined";
import Person2OutlinedIcon from "@mui/icons-material/Person2Outlined";
import { useSelector } from "react-redux";

const settings = ["Profile", "Account", "Dashboard", "Logout"];

const dashboard = [
  "Amazon Pay",
  "Today's Deals",
  "Buy Again",
  "Sell",
  "Gift Cards",
  "Baby",
];

const Header = () => {
  const { user } = useSelector((state) => state.authReducer);

  const { cartItems } = useSelector((state) => state.cartReducer);
  // cart count
  const getCartCount = () => cartItems.reduce((ac, el) => ac + el.qty, 0);

  const [anchorElNav, setAnchorElNav] = React.useState(null);
  const [anchorElUser, setAnchorElUser] = React.useState(null);

  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };
  const handleOpenUserMenu = (event) => {
    setAnchorElUser(event.currentTarget);
  };

  const handleCloseNavMenu = () => {
    setAnchorElNav(null);
  };

  const handleCloseUserMenu = () => {
    setAnchorElUser(null);
  };

  return (
    <header className="sticky top-0 z-10">
      <Nav>
        <NavLineOne className="text-xs">
          <NavLeftOptions>
            {/* mobile design */}
            <BurgerIcon className="flex md:hidden">
              <IconButton
                sx={{ p: 0 }}
                onClick={handleOpenNavMenu}
                color="inherit"
              >
                <MenuIcon fontSize="large" />
              </IconButton>

              <Menu
                anchorEl={anchorElNav}
                open={Boolean(anchorElNav)}
                onClose={handleCloseNavMenu}
              >
                <MenuItem onClick={handleCloseNavMenu}>
                  <Link to="/login">
                    <Typography className="link">
                      <OptionLineOne>
                        Hello,{user ? user.name.split(" ")[0] : "sign in"}
                      </OptionLineOne>
                      <OptionLineTwo>Account & Lists</OptionLineTwo>
                    </Typography>
                  </Link>
                </MenuItem>

                <MenuItem onClick={handleCloseNavMenu}>
                  <Typography className="link">
                    <OptionLineOne>Returns</OptionLineOne>
                    <OptionLineTwo>& Orders</OptionLineTwo>
                  </Typography>
                </MenuItem>
              </Menu>
            </BurgerIcon>
            {/* desktop design */}
            <Link to="/">
              <NavLogo>
                <img alt="brand-logo" src="https://i.imgur.com/7I9Was5.png" />
              </NavLogo>
            </Link>

            <NavOptionAddress>
              <LocationOnIcon />
              <NavOption className="link">
                <OptionLineOne>Hello</OptionLineOne>
                <OptionLineTwo>Select Your Address</OptionLineTwo>
              </NavOption>
            </NavOptionAddress>
          </NavLeftOptions>

          <NavSearch className="hidden md:flex">
            <NavSearchInput placeholder="Search Amazon.in" type="text" />
            <NavSearchIcon>
              <SearchIcon />
            </NavSearchIcon>
          </NavSearch>

          <NavRightOptions>
            {/* desktop view */}
            <NavItems className="hidden md:flex">
              <NavOption className="link">
                <Link to="/login" style={{ textDecoration: "none" }}>
                  <OptionLineOne>
                    Hello, {user ? user.name.split(" ")[0] : "sign in"}
                  </OptionLineOne>
                  <OptionLineTwo>Account & Lists</OptionLineTwo>
                </Link>
              </NavOption>

              <NavOption className="link">
                <OptionLineOne>Returns</OptionLineOne>
                <OptionLineTwo>& Orders</OptionLineTwo>
              </NavOption>
            </NavItems>

            {/* mobile view */}
            <UserAvatar className="flex md:hidden">
              <IconButton
                style={{
                  padding: 0,
                }}
                onClick={handleOpenUserMenu}
              >
                <p className="text-white font-bold text-xs flex">
                  {user ? user.name.split(" ")[0] : "Sign in"}
                  <KeyboardArrowRightIcon
                    sx={{ width: 18, height: 18 }}
                    style={{ padding: 0, color: "white" }}
                    fontSize="small"
                  />
                </p>

                <Avatar
                  sx={{ width: 35, height: 35 }}
                  alt="Remy Sharp"
                  src={user ? user.avatar : ""}
                >
                  {!user && <Person2OutlinedIcon style={{ color: "white" }} />}
                </Avatar>
              </IconButton>
              <Menu
                anchorEl={anchorElUser}
                open={Boolean(anchorElUser)}
                onClose={handleCloseUserMenu}
              >
                {settings.map((setting) => (
                  <MenuItem key={setting} onClick={handleCloseUserMenu}>
                    <Typography textAlign="center">{setting}</Typography>
                  </MenuItem>
                ))}
              </Menu>
            </UserAvatar>

            {/* cart */}
            <Link to="/cart">
              <NavOptionCart>
                <span className="absolute top-0 right-0 md:right-11 h-4 w-4 bg-yellow-400 text-black font-bold rounded-full text-center">
                  {getCartCount()}
                </span>
                <ShoppingCartOutlinedIcon fontSize="large" />
                <p className="hidden md:inline text-xs font-extrabold link mt-1">
                  Basket
                </p>
              </NavOptionCart>
            </Link>
          </NavRightOptions>
        </NavLineOne>

        {/* Mobile view search */}
        <NavSearch className="flex md:hidden">
          <NavSearchInput placeholder="Search Amazon.in" type="text" />
          <NavSearchIcon>
            <SearchIcon />
          </NavSearchIcon>
        </NavSearch>
      </Nav>

      {/* Dashboard */}
      <Dashboard className=" text-white xs-font text-xs p-2 text-gray flex ">
        <div className="items-center space-x-3 hidden md:flex">
          <p className="flex items-center space-x-2">
            <MenuIcon fontSize="small" />
            All
          </p>
          {dashboard.map((el, i) => (
            <p key={i} className="link">
              {el}
            </p>
          ))}
        </div>
        <p className="flex md:hidden items-center">
          <LocationOnIcon />
          {true
            ? "Deliver to Hashmat - Baramula 193101"
            : "Select a location to see product availability"}
        </p>
      </Dashboard>
    </header>
  );
};
