import React, { useState } from "react";
import styled from "styled-components";
// import { db } from "../../db/firebase";
// import { setDoc, doc, collection } from "firebase/firestore";
import { useSelector } from "react-redux";
import axios from "axios";
import FormGroup from "@mui/material/FormGroup";
import FormLabel from "@mui/material/FormLabel";

import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import IconButton from "@mui/material/IconButton";
import FormControlLabel from "@mui/material/FormControlLabel";
import Switch from "@mui/material/Switch";

// import Input from "@mui/material/Input";
import FilledInput from "@mui/material/FilledInput";
import OutlinedInput from "@mui/material/OutlinedInput";
import InputLabel from "@mui/material/InputLabel";
import InputAdornment from "@mui/material/InputAdornment";
import FormHelperText from "@mui/material/FormHelperText";
import FormControl from "@mui/material/FormControl";
import Visibility from "@mui/icons-material/Visibility";
import VisibilityOff from "@mui/icons-material/VisibilityOff";
import Stack from "@mui/material/Stack";
import Checkbox from "@mui/material/Checkbox";

const AddProduct = () => {
  // const { products } = useSelector((state) => state.productsReducer);

  const [formData, setformData] = useState({
    category: [],
  });
  const [errors, setErrors] = useState({});
  const [sameDPrice, setSameDPrice] = useState(false);
  const categories = [
    "men",
    "women",
    "electronics",
    "footwear",
    "clothing",
    "home",
  ];
  const tags = ["bestseller", "amazon_choice", "new", "false"];

  const handleFormData = (e) => {
    console.log(e.type);
    const { name, value, checked, type } = e.target;
    console.log(name, value, checked, type);





    // console.log(type, value);
    if (checked && name === "category") {
      setformData({
        ...formData,
        [name]: [...formData[name], value],
      });
    } else if (!checked && name === "category") {
      setformData({
        ...formData,
        [name]: formData[name].filter((e) => e !== value),
      });
    } else {
      setformData({
        ...formData,
        [name]:
          type === "checkbox"?chec
            : type === "number"
            ? +value
            : value,
      });
    }
  };

  const validate = () => {
    let temp = {};
    temp.title = formData.title ? null : "Title is required";
    temp.image = formData.image ? null : "Image is required";
    temp.org_price = formData.org_price ? null : "Original Price is required";
    temp.disc_price = formData.disc_price ? null : "Discount Price is required";
    temp.qty = formData.qty ? null : "Quantity is required";
    temp.expected_delivery_duration = formData.expected_delivery_duration
      ? null
      : "Expected Delivery Duration is required";
    setErrors(temp);

    return Object.values(temp).every((el) => el === null);
  };

  const addProduct = async (e) => {
    e.preventDefault();
    validate();
    axios
      .post("http://localhost:8080/addproduct", formData)
      .then((data) => console.log(data))
      .catch((err) => console.log(err.message));

    // setDoc(doc(productsColl, String(products.length + 1)), formData, {
    //   merge: true,
    // })
    //   .then((res) => console.log("result,", res))
    //   .catch((err) => console.log("err,", err));
  };
  return (
    <Container className="border">
      <pre>{JSON.stringify(formData, undefined, 2)}</pre>
      <form onSubmit={addProduct} noValidate>
        <fieldset>
          <Stack
            // direction={{ xs: "column", sm: "row" }}
            spacing={{ xs: 2, sm: 2, md: 4 }}
          >
            <TextField
              required
              name="title"
              value={formData.title}
              {...(errors.title
                ? { error: true, label: "Title Required" }
                : { label: "Title" })}
              variant="outlined"
              size="small"
              // InputLabelProps={{
              //   shrink: true,
              // }}
              onChange={handleFormData}
            />

            <TextField
              required
              name="image"
              {...(errors.image
                ? { error: true, label: errors.image }
                : { label: "Image URL" })}
              variant="outlined"
              size="small"
              onChange={handleFormData}
            />

            <FormLabel>Category</FormLabel>
            <FormGroup aria-label="position" row>
              {categories.map((cat, i) => (
                <FormControlLabel
                  value="start"
                  control={<Checkbox />}
                  label={cat}
                  // labelPlacement="start"
                />
              ))}
            </FormGroup>

            <FormControl size="small">
              <InputLabel {...(errors.org_price && { error: true })}>
                {errors.org_price
                  ? "Original Price Required"
                  : "Original Price"}
              </InputLabel>
              <OutlinedInput
                required
                name="org_price"
                {...(errors.org_price
                  ? { error: true, label: "Original Price Required" }
                  : { label: "Original Price" })}
                variant="outlined"
                size="small"
                type="number"
                step="1"
                onkeypress="return event.charCode >= 48 && event.charCode <= 57"
                onChange={handleFormData}
                startAdornment={
                  <InputAdornment position="start">₹</InputAdornment>
                }
              />
            </FormControl>

            <FormControl style={{ display: "flex" }} size="small">
              <InputLabel {...(errors.disc_price && { error: true })}>
                {errors.disc_price
                  ? "Discount Price Required"
                  : "Discount Price"}
              </InputLabel>

              <OutlinedInput
                disabled={sameDPrice}
                type="number"
                required
                name="disc_price"
                {...(errors.disc_price
                  ? { error: true, label: "Discount Price Required" }
                  : { label: "Discount Price" })}
                variant="outlined"
                size="small"
                value={sameDPrice ? formData.org_price : null}
                onChange={handleFormData}
                startAdornment={
                  <InputAdornment position="start">₹</InputAdornment>
                }
                endAdornment={
                  <InputAdornment position="end">
                    <FormControlLabel
                      value="toggle"
                      control={<Switch name="disc_price" />}
                      label="Same as original price"
                      labelPlacement="start"
                      onChange={() => {
                        setSameDPrice(!sameDPrice);
                        handleFormData();
                      }}
                    />
                  </InputAdornment>
                }
              />
            </FormControl>

            <FormControl size="small">
              <InputLabel {...(errors.coupon && { error: true })}>
                {errors.coupon ? "Coupon Required" : "Coupon"}
              </InputLabel>
              <OutlinedInput
                required
                name="coupon"
                {...(errors.coupon
                  ? { error: true, label: "Coupon Required" }
                  : { label: "Coupon" })}
                variant="outlined"
                size="small"
                type="number"
                step="1"
                onkeypress="return event.charCode >= 48 && event.charCode <= 57"
                onChange={handleFormData}
                startAdornment={
                  <InputAdornment position="start">₹</InputAdornment>
                }
              />
            </FormControl>

            <TextField
              required
              name="expected_delivery_duration"
              {...(errors.expected_delivery_duration
                ? { error: true, label: errors.expected_delivery_duration }
                : { label: "Expected Delivery Duration" })}
              variant="outlined"
              size="small"
              type="number"
              onChange={handleFormData}
            />

            <TextField
              required
              name="qty"
              {...(errors.qty
                ? { error: true, label: errors.qty }
                : { label: "Quantity" })}
              variant="outlined"
              size="small"
              type="number"
              onChange={handleFormData}
            />

            <FormControlLabel
              value="toggle"
              control={<Switch name="prime" />}
              label="Prime"
              labelPlacement="start"
              onChange={handleFormData}
            />
            <FormControlLabel
              value="toggle"
              control={<Switch name="first_delivery_free" />}
              label="First Delivery Free"
              labelPlacement="start"
              onChange={handleFormData}
            />
            {/* 
          <h4>Category:</h4>
          {categories.map((cat, i) => (
            <Input key={i}>
              <input
                onInput={handleFormData}
                type="checkbox"
                name="category"
                value={cat}
              />
              {cat}
            </Input>
          ))}

          <Input>
            Coupon:
            <input onChange={handleFormData} type="number" name="coupon" />
          </Input>

          <Input>
            Delivery Duration:
            <input
              onChange={handleFormData}
              type="number"
              name="delivery_duration"
            />
          </Input>

          <Input>
            Discount Price:
            <input
              onChange={handleFormData}
              type="number"
              name="discount_price"
              step=".01"
            />
          </Input>

          <Input>
            first Delivery Free:
            <input
              onInput={handleFormData}
              type="checkbox"
              name="first_delivery_free"
              value="first_delivery_free"
            />
          </Input>

          <Input>
            Image:
            <input onChange={handleFormData} type="text" name="image" />
          </Input>
          <Input>
            Price:
            <input
              onChange={handleFormData}
              type="number"
              name="price"
              step=".01"
            />
          </Input>
          <Input>
            Prime:
            <input
              onInput={handleFormData}
              type="checkbox"
              name="prime"
              value="prime"
            />
          </Input>
          <Input>
            Quantity:
            <input onChange={handleFormData} type="number" name="qty" />
          </Input>
          <Input>
            Rating:
            <input
              onChange={handleFormData}
              type="number"
              name="rating"
              step=".1"
            />
          </Input>

          <h4>Tag:</h4>

          {tags.map((tag, i) => (
            <Input key={i}>
              <input
                onInput={handleFormData}
                type="radio"
                name="tag"
                value={tag}
              />
              {tag}
            </Input>
          ))}

          <Input>
            Title:
            <input onChange={handleFormData} type="text" name="title" />
          </Input>*/}
            <Input>
              <input type="submit" value="submit" />
            </Input>
          </Stack>
        </fieldset>
      </form>
    </Container>
  );
};

export default AddProduct;

const Container = styled.div`
  padding: 10px;
`;

const Input = styled.div``;
