import React, { useState } from "react";
import styled from "styled-components";
// import { db } from "../../db/firebase";
// import { setDoc, doc, collection } from "firebase/firestore";
import { useSelector } from "react-redux";
import axios from "axios";

import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import IconButton from "@mui/material/IconButton";
// import Input from "@mui/material/Input";
import FilledInput from "@mui/material/FilledInput";
import OutlinedInput from "@mui/material/OutlinedInput";
import InputLabel from "@mui/material/InputLabel";
import InputAdornment from "@mui/material/InputAdornment";
import FormHelperText from "@mui/material/FormHelperText";
import FormControl from "@mui/material/FormControl";
import Visibility from "@mui/icons-material/Visibility";
import VisibilityOff from "@mui/icons-material/VisibilityOff";

const AddProduct = () => {
  // const { products } = useSelector((state) => state.productsReducer);

  const [formData, setformData] = useState({
    category: [],
  });
  const [errors, setErrors] = useState({});
  const [focused, setFocused] = useState("false");

  // const productsColl = collection(db, "products");

  const categories = [
    "men",
    "women",
    "electronics",
    "footwear",
    "clothing",
    "home",
  ];
  const tags = ["bestseller", "amazon_choice", "new", "false"];

  const handleFormData = (e) => {
    console.log(e);
    const { name, value, checked, type } = e.target;

    // console.log(type, value);
    if (checked && name === "category") {
      setformData({
        ...formData,
        [name]: [...formData[name], value],
      });
    } else if (!checked && name === "category") {
      setformData({
        ...formData,
        [name]: formData[name].filter((e) => e !== value),
      });
    } else {
      setformData({
        ...formData,
        [name]:
          type === "checkbox" ? checked : type === "number" ? +value : value,
      });
    }
  };

  const validate = () => {
    let temp = {};
    temp.title = formData.title ? null : "Title is required";
    temp.image = formData.image ? null : "Image is required";
    temp.org_price = formData.org_price ? null : "Original Price is required";
    temp.disc_price = formData.disc_price ? null : "Discount Price is required";
    temp.qty = formData.qty ? null : "Quantity is required";
    temp.expected_delivery_duration = formData.expected_delivery_duration
      ? null
      : "Expected Delivery Duration is required";
    setErrors(temp);

    return Object.values(temp).every((el) => el === null);
  };

  const addProduct = async (e) => {
    e.preventDefault();
    validate();
    axios
      .post("http://localhost:8080/addproduct", formData)
      .then((data) => console.log(data))
      .catch((err) => console.log(err.message));

    // setDoc(doc(productsColl, String(products.length + 1)), formData, {
    //   merge: true,
    // })
    //   .then((res) => console.log("result,", res))
    //   .catch((err) => console.log("err,", err));
  };
  return (
    <Container className="border">
      <pre>{JSON.stringify(formData, undefined, 2)}</pre>
      <form onSubmit={addProduct}>
        <fieldset>
         
            <TextField
              required
              name="title"
              value={formData.title}
              {...(errors.title
                ? { error: true, label: "Title Required" }
                : { label: "Title" })}
              // placeholder="Title"
              // label={"Required"}
              variant="outlined"
              size="small"
              // InputLabelProps={{
              //   shrink: true,
              // }}
              onChange={handleFormData}
            />

            <TextField
              required
              name="image"
              value={formData.image}
              {...(errors.image
                ? { error: true, label: "Required" }
                : { label: "Image URL" })}
              variant="outlined"
              size="small"
              onChange={handleFormData}
            />

            <FormControl size="small">
              <InputLabel {...(errors.org_price && { error: true })}>
                {errors.org_price
                  ? "Original Price Required"
                  : "Original Price"}
              </InputLabel>
              <OutlinedInput
                required
                name="org_price"
                {...(errors.org_price
                  ? { error: true, label: "Original Price Required" }
                  : { label: "Original Price" })}
                variant="outlined"
                size="small"
                value={formData.org_price}
                onChange={handleFormData}
                startAdornment={
                  <InputAdornment position="start">₹</InputAdornment>
                }
              />
            </FormControl>

            <FormControl size="small">
              <InputLabel {...(errors.disc_price && { error: true })}>
                {errors.disc_price
                  ? "Discount Price Required"
                  : "Discount Price"}
              </InputLabel>
              <OutlinedInput
                required
                name="disc_price"
                {...(errors.disc_price
                  ? { error: true, label: "Discount Price Required" }
                  : { label: "Discount Price" })}
                variant="outlined"
                size="small"
                value={formData.disc_price}
                onChange={handleFormData}
                startAdornment={
                  <InputAdornment position="start">₹</InputAdornment>
                }
              />
            </FormControl>



          <h4>Category:</h4>
          {categories.map((cat, i) => (
            <Input key={i}>
              <input
                onInput={handleFormData}
                type="checkbox"
                name="category"
                value={cat}
              />
              {cat}
            </Input>
          ))}

          <Input>
            Coupon:
            <input onChange={handleFormData} type="number" name="coupon" />
          </Input>

          <Input>
            Delivery Duration:
            <input
              onChange={handleFormData}
              type="number"
              name="delivery_duration"
            />
          </Input>

          <Input>
            Discount Price:
            <input
              onChange={handleFormData}
              type="number"
              name="discount_price"
              step=".01"
            />
          </Input>

          <Input>
            first Delivery Free:
            <input
              onInput={handleFormData}
              type="checkbox"
              name="first_delivery_free"
              value="first_delivery_free"
            />
          </Input>

          <Input>
            Image:
            <input onChange={handleFormData} type="text" name="image" />
          </Input>
          <Input>
            Price:
            <input
              onChange={handleFormData}
              type="number"
              name="price"
              step=".01"
            />
          </Input>
          <Input>
            Prime:
            <input
              onInput={handleFormData}
              type="checkbox"
              name="prime"
              value="prime"
            />
          </Input>
          <Input>
            Quantity:
            <input onChange={handleFormData} type="number" name="qty" />
          </Input>
          <Input>
            Rating:
            <input
              onChange={handleFormData}
              type="number"
              name="rating"
              step=".1"
            />
          </Input>

          <h4>Tag:</h4>

          {tags.map((tag, i) => (
            <Input key={i}>
              <input
                onInput={handleFormData}
                type="radio"
                name="tag"
                value={tag}
              />
              {tag}
            </Input>
          ))}

          <Input>
            Title:
            <input onChange={handleFormData} type="text" name="title" />
          </Input>
          <Input>
            <input type="submit" value="submit" />
          </Input>
        </fieldset>
      </form>

      <Box
        component="form"
        sx={{
          "& > :not(style)": { m: 1, width: "25ch" },
        }}
        noValidate
        autoComplete="off"
      >
        <TextField id="filled-basic" label="Filled" variant="filled" />
        <TextField id="standard-basic" label="Standard" variant="standard" />
      </Box>
    </Container>
  );
};

export default AddProduct;

const Container = styled.div`
  padding: 10px;
`;

const Input = styled.div``;
