// import { db } from "../../db/firebase";
// import { setDoc, doc, collection } from "firebase/firestore";
import React, { useState } from "react";
import styled from "styled-components";
import axios from "axios";
import FormGroup from "@mui/material/FormGroup";
import FormLabel from "@mui/material/FormLabel";
import Radio from "@mui/material/Radio";
import RadioGroup from "@mui/material/RadioGroup";
import TextField from "@mui/material/TextField";
import FormControlLabel from "@mui/material/FormControlLabel";
import Switch from "@mui/material/Switch";
import OutlinedInput from "@mui/material/OutlinedInput";
import InputLabel from "@mui/material/InputLabel";
import InputAdornment from "@mui/material/InputAdornment";
import FormControl from "@mui/material/FormControl";
import Stack from "@mui/material/Stack";
import Checkbox from "@mui/material/Checkbox";
import LoadingButton from "@mui/lab/LoadingButton";
import SaveIcon from "@mui/icons-material/Save";

const AddProduct = () => {
  const [formData, setformData] = useState({
    category: [],
    title: "",
    image: "",
    tag: "",
    org_price: "",
    disc_price: "",
    coupon: "",
    qty: "",
    prime: false,
    first_delivery_free: false,
    expected_delivery_duration: "",
  });
  const [errors, setErrors] = useState({});
  const [sameDPrice, setSameDPrice] = useState(false);
  const [loading, setLoading] = useState(false);
  const categories = [
    "men",
    "women",
    "electronics",
    "footwear",
    "clothing",
    "home",
  ];

  const tags = ["bestseller", "amazon_choice", "new"];

  const handleFormData = (e) => {
    const { name, value, checked, type } = e.target;

    //for storing the category array
    if (checked && name === "category") {
      setformData({
        ...formData,
        [name]: [...formData[name], value],
      });
    } else if (!checked && name === "category") {
      setformData({
        ...formData,
        [name]: formData[name].filter((e) => e !== value),
      });
      //making controlled radio buttons
    } else if (type === "radio" && value === formData.tag) {
      setformData({
        ...formData,
        tag: "",
      });
      //for storing input boxes
    } else {
      setformData({
        ...formData,
        [name]:
          type === "number" && value === ""
            ? ""
            : type === "number"
            ? +value
            : type === "checkbox"
            ? checked
            : value,
      });
    }
  };

  const validate = () => {
    let temp = {};

    temp.title = formData.title ? null : "Title is required";

    temp.image = formData.image ? null : "Image is required";

    temp.org_price = !formData.org_price
      ? "Original Price is required"
      : formData.org_price < 0
      ? "Original Price can't be negative"
      : !/^[1-9]\d*$/.test(formData.org_price)
      ? "Please give valid input without decimal"
      : null;

    temp.disc_price = !formData.disc_price
      ? "Discount Price is required"
      : formData.disc_price > formData.org_price
      ? "Discount Price can't be greater the original price"
      : formData.disc_price < 0
      ? "Discount Price can't be negative"
      : !/^[1-9]\d*$/.test(formData.disc_price)
      ? "Please give valid input without decimal"
      : null;

    temp.qty = formData.qty ? null : "Quantity is required";

    temp.expected_delivery_duration =
      formData.expected_delivery_duration < 0
        ? "days can't be in negative"
        : formData.expected_delivery_duration === null
        ? "Expected Delivery Duration is required"
        : null;

    temp.category = !formData.category.length
      ? "Select a category(minimum 1)"
      : null;

    setErrors(temp);

    return Object.values(errors).every((el) => el === null);
  };

  const addProduct = async (e) => {
    e.preventDefault();
    // setLoading(true);
    console.log("clicked");
    if (validate()) {
      // axios
      //   .post("http://localhost:8080/addproduct", formData)
      //   .then((data) => {
      //     console.log(data);
      //     setLoading(false);
      //   })
      //   .catch((err) => {
      //     console.log(err.message);
      //     setLoading(false);
      //   });
      // setDoc(doc(productsColl, String(products.length + 1)), formData, {
      //   merge: true,
      // })
      //   .then((res) => console.log("result,", res))
      //   .catch((err) => console.log("err,", err));
    }
  };

  return (
    <Container className="border">
      <pre>{JSON.stringify(formData, undefined, 2)}</pre>
      <form style={{textAlign:"cen"}}onSubmit={addProduct} noValidate>
        <Stack
          // direction={{ xs: "column", sm: "row" }}
          spacing={{ xs: 2, sm: 2, md: 4 }}
          sx={{ width: 400 }}
        >
          {/* Title */}
          <TextField
            required
            name="title"
            value={formData.title}
            {...(errors.title
              ? { error: true, label: errors.title }
              : { label: "Title" })}
            variant="outlined"
            size="small"
            onChange={handleFormData}
          />

          {/* Image */}
          <TextField
            required
            name="image"
            value={formData.image}
            {...(errors.image
              ? { error: true, label: errors.image }
              : { label: "Image URL" })}
            variant="outlined"
            size="small"
            onChange={handleFormData}
          />

          {/* Categories */}
          <FormLabel required error={errors.category ? true : false}>
            {errors.category ? errors.category : "Category"}
          </FormLabel>
          <FormGroup aria-label="position" row>
            {categories.map((cat, i) => (
              <FormControlLabel
                key={i}
                value={cat}
                control={<Checkbox />}
                name="category"
                label={cat}
                onInput={handleFormData}
              />
            ))}
          </FormGroup>

          {/* Tag */}
          <FormLabel>Tag</FormLabel>
          <RadioGroup row value={formData.tag}>
            {tags.map((tag, i) => (
              <FormControlLabel
                key={i}
                value={tag}
                control={<Radio />}
                name="tag"
                label={tag}
                onClick={handleFormData}
              />
            ))}
          </RadioGroup>

          {/* Original Price */}
          <FormControl size="small">
            <InputLabel required error={errors.org_price ? true : false}>
              {errors.org_price ? errors.org_price : "Original Price"}
            </InputLabel>
            <OutlinedInput
              required
              name="org_price"
              {...(errors.org_price
                ? { error: true, label: errors.org_price }
                : { label: "Original Price" })}
              variant="outlined"
              size="small"
              type="number"
              step="1"
              value={formData.org_price}
              onChange={handleFormData}
              startAdornment={
                <InputAdornment position="start">₹</InputAdornment>
              }
            />
          </FormControl>

          {/* Discount price */}
          <FormControl size="small">
            <InputLabel required error={errors.disc_price ? true : false}>
              {errors.disc_price ? errors.disc_price : "Discount Price"}
            </InputLabel>
            <OutlinedInput
              required
              disabled={sameDPrice}
              type="number"
              name="disc_price"
              {...(errors.disc_price
                ? { error: true, label: errors.disc_price }
                : { label: "Discount Price" })}
              variant="outlined"
              size="small"
              value={sameDPrice ? formData.org_price : formData.disc_price}
              onChange={handleFormData}
              startAdornment={
                <InputAdornment position="start">₹</InputAdornment>
              }
              endAdornment={
                <InputAdornment position="end">
                  <FormControlLabel
                    disabled={formData.org_price ? false : true}
                    value={sameDPrice ? "" : formData.org_price}
                    control={<Switch name="disc_price" />}
                    label="Same as original price"
                    labelPlacement="start"
                    onChange={(e) => {
                      setSameDPrice(!sameDPrice);
                      handleFormData(e);
                    }}
                  />
                </InputAdornment>
              }
            />
          </FormControl>

          {/* Coupon */}
          <FormControl size="small">
            <InputLabel required error={errors.coupon ? true : false}>
              {errors.coupon ? errors.coupon : "Coupon"}
            </InputLabel>
            <OutlinedInput
              required
              name="coupon"
              {...(errors.coupon
                ? { error: true, label: errors.coupon }
                : { label: "Coupon" })}
              variant="outlined"
              size="small"
              type="number"
              step="1"
              onChange={handleFormData}
              value={formData.coupon}
              startAdornment={
                <InputAdornment position="start">%</InputAdornment>
              }
            />
          </FormControl>

          {/* Expected delivery duration */}
          <FormControl size="small">
            <InputLabel
              required
              error={errors.expected_delivery_duration ? true : false}
            >
              {errors.expected_delivery_duration
                ? errors.expected_delivery_duration
                : "Expected Delivery Duration"}
            </InputLabel>
            <OutlinedInput
              required
              name="expected_delivery_duration"
              {...(errors.expected_delivery_duration
                ? {
                    error: true,
                    label: errors.expected_delivery_duration,
                  }
                : { label: "Expected Delivery Duration" })}
              variant="outlined"
              size="small"
              type="number"
              step="1"
              value={formData.expected_delivery_duration}
              onChange={handleFormData}
              endAdornment={
                <InputAdornment position="end">In Days</InputAdornment>
              }
            />
          </FormControl>

          {/* Quantity */}
          <TextField
            required
            name="qty"
            {...(errors.qty
              ? { error: true, label: errors.qty }
              : { label: "Quantity" })}
            variant="outlined"
            size="small"
            type="number"
            value={formData.qty}
            onChange={handleFormData}
          />

          {/* Prime */}
          <FormControlLabel
            control={<Switch name="prime" />}
            label="Prime"
            labelPlacement="start"
            onChange={handleFormData}
          />

          {/* First delivery free */}
          <FormControlLabel
            control={<Switch name="first_delivery_free" />}
            label="First Delivery Free"
            labelPlacement="start"
            onChange={handleFormData}
          />

          {/* <Button type="submit" variant="contained">
            Contained
          </Button> */}
          <LoadingButton
            type="submit"
            loading={loading}
            loadingPosition="start"
            startIcon={<SaveIcon />}
            variant="outlined"
          >
            Save
          </LoadingButton>
        </Stack>
      </form>
    </Container>
  );
};

export default AddProduct;

const Container = styled.div`
  padding: 10px;
`;
